function enableAccordion(){$(".accordion").on("click",(function(){const panel=$(this).next();$(this).toggleClass("active"),panel.slideToggle(200)}))}window.onload=function(){$.ajax({type:"GET",url:"https://raw.githubusercontent.com/terrabrasilis/FAQ/main/terrabrasilis-faq-pt.json",dataType:"json",success:function(json){const container=$("#faq-itens");Object.entries(json.faq).forEach(([key,section])=>{let html=`\n          <div class="category">\n            <button class="accordion">\n              ${section.title}\n              <span class="icon"></span>\n            </button>\n            <div class="panel">\n        `;section.itens.forEach(item=>{html+=`\n            <div class="question-item">\n              <h3 class="faq-heading">${item.question}</h3>\n              <p class="faq-text">${item.answer}</p>\n            </div>\n          `}),html+="\n            </div>\n          </div>\n        ",container.append(html)}),enableAccordion()},error:function(err){console.error("Erro ao carregar o FAQ",err)}})};